mapper.select:
select concat(env.yun_harbor_host, tem.harbor_url)
from module_temp tem
         left join module_manage man on man.module_name = tem.module_name
         left join module_env env on env.id = man.env_id
where man.env_id = #{envId,jdbcType=INTEGER}
  and man.id = #{moduleId,jdbcType=INTEGER}

SELECT a.id AS moduleId,a.module_name AS moduleName,a.module_content_name AS moduleContentName, a.module_type AS moduleType,b.center_name AS centerName,a.create_time AS createTime, a.module_project_code AS moduleProjectCode,b.id AS centerId,a.env_id AS envId,a.mark AS mark, a.charge_person AS chargePerson,b.child_center_name AS childCenterName FROM module_manage a LEFT JOIN module_center b ON a.center_id = b.id
a.is_delete = 0 AND b.is_deleted = 0
AND (a.module_name LIKE concat('%',#{moduleName,jdbcType=VARCHAR},'%') OR a.module_content_name LIKE concat('%',#{moduleName,jdbcType=VARCHAR},'%'))
AND b.id = #{centerId,jdbcType=INTEGER}
AND a.env_id = #{envId,jdbcType=INTEGER}
AND a.module_type = #{moduleType,jdbcType=INTEGER}
AND a.create_time >= #{beginTime,jdbcType=VARCHAR}
AND a.create_time <= #{endTime,jdbcType=VARCHAR}
AND a.env_id IN
#{envId}
ORDER BY a.create_time DESC

select
from module_manage where id IN
#{id}

SELECT count(1)
FROM module_manage a
WHERE a.is_delete = 0
  AND a.env_id = #{envId,jdbcType=INTEGER}
  AND a.center_id = #{centerId,jdbcType=INTEGER}
  AND (a.module_name = #{moduleName,jdbcType=VARCHAR}
    OR a.module_content_name = #{moduleContentName,jdbcType=VARCHAR})

select id, module_name AS moduleName, env_id AS envId FROM module_manage where
module_name IN
#{moduleName}
